import React from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import A from "./img/a1.jpg";
import B from "./img/a2.jpg";
import C from "./img/a3.jpg";
import D from "./img/a4.jpg";

const Courses = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 3
  };
  const courses = [A, C, B, D, B, A];
  return (
    <div>
      <div className='p-2'>
        <h2 className='text-xl'> Running Courses </h2>
        <Slider className='m-4 p-2' dots= 'true'
    infinite= 'true' speed= '500' slidesToShow='5'>
          {courses.map((v) => {
            return (
              <div className="bg-white rounded shadow max-w-xl border-4">
                <img
                  className="w-full h-32 sm:h-48 object-cover"
                  src={v}
                  alt=""
                />
                <div className="m-2 p-2 w-48 md:w-64">
                  <h1 className="font-bold text-base">English For Today</h1>
                  <span>XI-XII</span>
                </div>
              </div>
            )
          })}
        </Slider>
      </div>
    </div>
  );
};

export default Courses;
