import React, {Component, Fragment} from 'react';
import {Nav, Navbar, NavLink} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {
    FaBars,
    FaBookOpen,
    FaCode,
    FaComment,
    faEnvelope,
    faFolder,
    faHome,
    faPowerOff, faSearch, faUser
} from "react-icons/fa";
import {Link} from "react-router-dom";

class SideNav extends Component {
    constructor() {
        super();
        this.state={
            sideNav:false,
            sideNavClass:"sidenavClose",
            NavText:"d-none",
            mainDivOverlay:"main-overlay-close"
        }

        this.showHideSideNav=this.showHideSideNav.bind(this);
    }


    showHideSideNav(){
        if(this.state.sideNav===false){
            this.setState({sideNav:true,NavText:"",sideNavClass:"sidenavOpen",mainDivOverlay:"main-overlay-open"})
        }
        else {
            this.setState({sideNav:false,NavText:"d-none",sideNavClass:"sidenavClose",mainDivOverlay:"main-overlay-close"})
        }
    }


    render() {
        return (
            <Fragment>
                <Navbar  expand="lg" className="fixed-top shadow-sm bg-white mb-5 py-3" variant="light" bg="white">
                    <Navbar.Brand onClick={this.showHideSideNav}  href="#"><FontAwesomeIcon icon={FaBars} /> DASHBOARD</Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <div className="searchBox my-1">
                            <input type="text" className="searchBar" placeholder="  Search.."/>
                            <button className="searchBtn ml-2"><FontAwesomeIcon icon={faSearch} /></button>
                        </div>
                        <Nav className="logoutUserWrap">
                            <button className="userIcon"><FontAwesomeIcon icon={faUser}  /></button>
                            <button className="logoutBtn">Logout</button>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>

                <div className={this.state.sideNavClass}>
                    <NavLink> <Link className="NavItem" to=""> <FontAwesomeIcon icon={faHome} /> <span className={this.state.NavText}>Dashboard</span> </Link></NavLink>
                    <NavLink><Link className="NavItem" to=""> <FontAwesomeIcon icon={faEnvelope} /> <span className={this.state.NavText}>Site Home</span></Link></NavLink>
                    <NavLink><Link className="NavItem" to=""> <FontAwesomeIcon icon={FaBookOpen} /> <span className={this.state.NavText}>Courses</span></Link></NavLink>
                    <NavLink><Link className="NavItem" to=""> <FontAwesomeIcon icon={FaCode} /> <span className={this.state.NavText}>Projects</span></Link></NavLink>
                    <NavLink><Link className="NavItem" to=""> <FontAwesomeIcon icon={faFolder} /> <span className={this.state.NavText}>Notice</span></Link></NavLink>
                    <NavLink><Link className="NavItem" to=""> <FontAwesomeIcon icon={FaComment} /> <span className={this.state.NavText}>Help</span></Link></NavLink>
                    <a className=" ml-3 NavItem" href="#"> <FontAwesomeIcon icon={faPowerOff} /> <span className={this.state.NavText}>Sign Out</span></a>
                </div>
                <div onClick={this.showHideSideNav} className={this.state.mainDivOverlay}>

                </div>

            </Fragment>
        );
    }
}

export default SideNav;
